def obtener_numero(mensaje):
    """Obtiene un n√∫mero del usuario con validaci√≥n"""
    while True:
        try:
            entrada = input(mensaje)
            numero = float(entrada)
            return numero
        except ValueError:
            print(f'Error: "{entrada}" no es un n√∫mero v√°lido')
            print('Por favor, introduce un n√∫mero (ej: 5, 3.14, -2)')
        except KeyboardInterrupt:
            print('\nOperaci√≥n cancelada por el usuario')
            return None

def obtener_operador():
    """Obtiene un operador v√°lido del usuario"""
    operadores_validos = ['+', '-', '*', '/']
    while True:
        operador = input('Introduce el operador (+, -, *, /): ').strip()
        if operador in operadores_validos:
            return operador
        else:
            print(f'Error: "{operador}" no es un operador v√°lido')
            print('Operadores v√°lidos: +, -, *, /')

def realizar_operacion(num1, num2, operador):
    """Realiza operaci√≥n matem√°tica con manejo de errores"""
    try:
        if operador == '+':
            return num1 + num2
        elif operador == '-':
            return num1 - num2
        elif operador == '*':
            return num1 * num2
        elif operador == '/':
            if num2 == 0:
                raise ZeroDivisionError('No se puede dividir por cero')
            return num1 / num2
    except ZeroDivisionError as e:
        print(f'Error de divisi√≥n: {e}')
        return None
    except Exception as e:
        print(f'Error inesperado: {e}')
        return None

def calculadora_principal():
    """Funci√≥n principal de la calculadora"""
    print('=== CALCULADORA ROBUSTA ===')
    print('Operadores disponibles: +, -, *, /')
    print('Presiona Ctrl+C para cancelar en cualquier momento\n')

    try:
        # Obtener primer n√∫mero
        num1 = obtener_numero('Introduce el primer n√∫mero: ')
        if num1 is None:
            return

        # Obtener operador
        operador = obtener_operador()

        # Obtener segundo n√∫mero
        num2 = obtener_numero('Introduce el segundo n√∫mero: ')
        if num2 is None:
            return

        # Realizar operaci√≥n
        resultado = realizar_operacion(num1, num2, operador)

        if resultado is not None:
            print(f'\n=== RESULTADO ===')
            print(f'{num1} {operador} {num2} = {resultado}')

            # Informaci√≥n adicional
            if operador == '/':
                print(f'Resultado redondeado: {round(resultado, 2)}')
        else:
            print('\nNo se pudo completar la operaci√≥n')

    except KeyboardInterrupt:
        print('\n\nCalculadora cerrada por el usuario')
    except Exception as e:
        print(f'\nError inesperado en la calculadora: {e}')
    finally:
        print('\n¬°Gracias por usar la calculadora!')

# Simulaci√≥n de la calculadora (en el ejercicio real usar√≠as input())
print('=== DEMOSTRACI√ìN ===')
print('Simulando: 10 / 2')
resultado_demo = realizar_operacion(10, 2, '/')
print(f'Resultado: {resultado_demo}')

print('\nSimulando: 10 / 0 (error)')
resultado_error = realizar_operacion(10, 0, '/')
print(f'Resultado: {resultado_error}')

print('\nSimulando operador inv√°lido')
try:
    resultado_invalido = realizar_operacion(5, 3, '%')
except:
    print('Operador no soportado')

# Para ejecutar la calculadora interactiva, descomenta:
