# Clase base (padre)
class Animal:
    def __init__(self, nombre, especie):
        self.nombre = nombre
        self.especie = especie
        self.energia = 100

    def comer(self):
        self.energia += 20
        return f'{self.nombre} est√° comiendo. Energ√≠a: {self.energia}'

    def dormir(self):
        self.energia = 100
        return f'{self.nombre} durmi√≥ y recuper√≥ toda su energ√≠a'

    def hacer_sonido(self):  # m√©todo que ser√° sobrescrito
        return f'{self.nombre} hace un sonido'

# Clases derivadas (hijos)
class Perro(Animal):
    def __init__(self, nombre, raza):
        super().__init__(nombre, 'Perro')  # llamar constructor padre
        self.raza = raza

    def hacer_sonido(self):  # sobrescribir m√©todo
        return f'{self.nombre} ladra: ¬°Guau guau!'

    def traer_pelota(self):
        self.energia -= 15
        return f'{self.nombre} trajo la pelota. Energ√≠a: {self.energia}'

class Gato(Animal):
    def __init__(self, nombre, color):
        super().__init__(nombre, 'Gato')
        self.color = color

    def hacer_sonido(self):
        return f'{self.nombre} ma√∫lla: ¬°Miau!'

    def trepar(self):
        self.energia -= 10
        return f'{self.nombre} trep√≥ un √°rbol. Energ√≠a: {self.energia}'

# Crear objetos
mi_perro = Perro('Rex', 'Labrador')
mi_gato = Gato('Whiskers', 'Negro')

# Polimorfismo: mismo m√©todo, comportamiento diferente
animales = [mi_perro, mi_gato]
for animal in animales:
    print(animal.hacer_sonido())  # cada uno hace su sonido espec√≠fico

