# Simulaci√≥n de cliente REST API
import json
from datetime import datetime

# Simulamos datos de una API de usuarios
api_usuarios_mock = {
    'usuarios': [
        {'id': 1, 'nombre': 'Ana Garc√≠a', 'email': 'ana@email.com', 'activo': True, 'registro': '2024-01-15'},
        {'id': 2, 'nombre': 'Carlos L√≥pez', 'email': 'carlos@email.com', 'activo': True, 'registro': '2024-02-20'},
        {'id': 3, 'nombre': 'Mar√≠a Rodr√≠guez', 'email': 'maria@email.com', 'activo': False, 'registro': '2024-01-10'}
    ],
    'productos': [
        {'id': 101, 'nombre': 'Laptop', 'precio': 999.99, 'stock': 50, 'categoria': 'tecnologia'},
        {'id': 102, 'nombre': 'Mouse', 'precio': 29.99, 'stock': 200, 'categoria': 'tecnologia'},
        {'id': 103, 'nombre': 'Libro Python', 'precio': 39.99, 'stock': 100, 'categoria': 'educacion'}
    ]
}

class APIClient:
    """Cliente simulado para APIs REST"""

    def __init__(self, base_url='https://api.ejemplo.com'):
        self.base_url = base_url
        self.headers = {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer token_ejemplo'
        }

    def get(self, endpoint):
        """Simula una petici√≥n GET"""
        print(f'üåê GET {self.base_url}{endpoint}')

        if endpoint == '/usuarios':
            return {
                'status': 200,
                'data': api_usuarios_mock['usuarios'],
                'message': 'Usuarios obtenidos exitosamente'
            }
        elif endpoint == '/productos':
            return {
                'status': 200,
                'data': api_usuarios_mock['productos'],
                'message': 'Productos obtenidos exitosamente'
            }
        elif endpoint.startswith('/usuarios/'):
            user_id = int(endpoint.split('/')[-1])
            usuario = next((u for u in api_usuarios_mock['usuarios'] if u['id'] == user_id), None)
            if usuario:
                return {'status': 200, 'data': usuario}
            else:
                return {'status': 404, 'error': 'Usuario no encontrado'}
        else:
            return {'status': 404, 'error': 'Endpoint no encontrado'}

    def post(self, endpoint, data):
        """Simula una petici√≥n POST"""
        print(f'üåê POST {self.base_url}{endpoint}')
        print(f'üì§ Datos enviados: {json.dumps(data, indent=2)}')

        if endpoint == '/usuarios':
            nuevo_id = max(u['id'] for u in api_usuarios_mock['usuarios']) + 1
            nuevo_usuario = {**data, 'id': nuevo_id, 'registro': datetime.now().strftime('%Y-%m-%d')}
            api_usuarios_mock['usuarios'].append(nuevo_usuario)
            return {
                'status': 201,
                'data': nuevo_usuario,
                'message': 'Usuario creado exitosamente'
            }
        else:
            return {'status': 400, 'error': 'Endpoint no v√°lido para POST'}

    def put(self, endpoint, data):
        """Simula una petici√≥n PUT"""
        print(f'üåê PUT {self.base_url}{endpoint}')

        if endpoint.startswith('/usuarios/'):
            user_id = int(endpoint.split('/')[-1])
            for i, usuario in enumerate(api_usuarios_mock['usuarios']):
                if usuario['id'] == user_id:
                    api_usuarios_mock['usuarios'][i] = {**usuario, **data}
                    return {
                        'status': 200,
                        'data': api_usuarios_mock['usuarios'][i],
                        'message': 'Usuario actualizado exitosamente'
                    }
            return {'status': 404, 'error': 'Usuario no encontrado'}
        else:
            return {'status': 400, 'error': 'Endpoint no v√°lido para PUT'}

    def delete(self, endpoint):
        """Simula una petici√≥n DELETE"""
        print(f'üåê DELETE {self.base_url}{endpoint}')

        if endpoint.startswith('/usuarios/'):
            user_id = int(endpoint.split('/')[-1])
            usuario_encontrado = None
            for i, usuario in enumerate(api_usuarios_mock['usuarios']):
                if usuario['id'] == user_id:
                    usuario_encontrado = api_usuarios_mock['usuarios'].pop(i)
                    break

            if usuario_encontrado:
                return {
                    'status': 200,
                    'message': f'Usuario {usuario_encontrado["nombre"]} eliminado exitosamente'
                }
            else:
                return {'status': 404, 'error': 'Usuario no encontrado'}
        else:
            return {'status': 400, 'error': 'Endpoint no v√°lido para DELETE'}

# Ejemplo de uso del cliente API
api = APIClient()

print('=== CLIENTE API REST SIMULADO ===')

# GET - Obtener todos los usuarios
print('\n1. Obteniendo todos los usuarios:')
response = api.get('/usuarios')
if response['status'] == 200:
    print(f'‚úÖ {response["message"]}')
    for usuario in response['data']:
        estado = 'üü¢ Activo' if usuario['activo'] else 'üî¥ Inactivo'
        print(f'   ‚Ä¢ {usuario["nombre"]} ({usuario["email"]}) - {estado}')
else:
    print(f'‚ùå Error: {response["error"]}')

# GET - Obtener un usuario espec√≠fico
print('\n2. Obteniendo usuario espec√≠fico (ID: 1):')
response = api.get('/usuarios/1')
if response['status'] == 200:
    usuario = response['data']
    print(f'‚úÖ Usuario encontrado:')
    print(f'   Nombre: {usuario["nombre"]}')
    print(f'   Email: {usuario["email"]}')
    print(f'   Registro: {usuario["registro"]}')
else:
    print(f'‚ùå Error: {response["error"]}')

# POST - Crear nuevo usuario
print('\n3. Creando nuevo usuario:')
nuevo_usuario = {
    'nombre': 'Luis Mart√≠n',
    'email': 'luis@email.com',
    'activo': True
}
response = api.post('/usuarios', nuevo_usuario)
if response['status'] == 201:
    print(f'‚úÖ {response["message"]}')
    print(f'   ID asignado: {response["data"]["id"]}')
    print(f'   Fecha de registro: {response["data"]["registro"]}')
else:
    print(f'‚ùå Error: {response["error"]}')

# PUT - Actualizar usuario
print('\n4. Actualizando usuario (ID: 2):')
actualizacion = {'email': 'carlos.nuevo@email.com', 'activo': False}
response = api.put('/usuarios/2', actualizacion)
if response['status'] == 200:
    print(f'‚úÖ {response["message"]}')
    usuario = response['data']
    print(f'   Nuevo email: {usuario["email"]}')
    print(f'   Estado: {"Activo" if usuario["activo"] else "Inactivo"}')
else:
    print(f'‚ùå Error: {response["error"]}')

# Ver estado final
print('\n5. Estado final de usuarios:')
response = api.get('/usuarios')
for usuario in response['data']:
    estado = 'üü¢' if usuario['activo'] else 'üî¥'
    print(f'   {estado} {usuario["id"]}. {usuario["nombre"]} - {usuario["email"]}')

