# Sistema de gesti√≥n de tareas con API simulada
import json
from datetime import datetime, timedelta

# Base de datos simulada
tareas_db = [
    {
        'id': 1,
        'titulo': 'Aprender Python',
        'descripcion': 'Completar curso de Python b√°sico',
        'prioridad': 'alta',
        'estado': 'en_progreso',
        'fecha_creacion': '2024-08-01',
        'fecha_vencimiento': '2024-08-15',
        'asignado_a': 'Ana Garc√≠a',
        'etiquetas': ['educacion', 'programacion']
    },
    {
        'id': 2,
        'titulo': 'Revisar c√≥digo',
        'descripcion': 'Hacer code review del proyecto web',
        'prioridad': 'media',
        'estado': 'pendiente',
        'fecha_creacion': '2024-08-02',
        'fecha_vencimiento': '2024-08-10',
        'asignado_a': 'Carlos L√≥pez',
        'etiquetas': ['desarrollo', 'revision']
    }
]

class GestorTareas:
    """Gestor de tareas con operaciones CRUD"""

    def __init__(self):
        self.tareas = tareas_db.copy()
        self.siguiente_id = max(tarea['id'] for tarea in self.tareas) + 1 if self.tareas else 1

    def obtener_todas_tareas(self):
        """GET /tareas - Obtiene todas las tareas"""
        return {
            'status': 200,
            'data': self.tareas,
            'total': len(self.tareas),
            'message': 'Tareas obtenidas exitosamente'
        }

    def obtener_tarea(self, tarea_id):
        """GET /tareas/{id} - Obtiene una tarea espec√≠fica"""
        tarea = next((t for t in self.tareas if t['id'] == tarea_id), None)
        if tarea:
            # Calcular d√≠as restantes
            fecha_venc = datetime.strptime(tarea['fecha_vencimiento'], '%Y-%m-%d')
            dias_restantes = (fecha_venc - datetime.now()).days

            tarea_extendida = {
                **tarea,
                'dias_restantes': dias_restantes,
                'vencida': dias_restantes < 0
            }

            return {
                'status': 200,
                'data': tarea_extendida,
                'message': 'Tarea encontrada'
            }
        else:
            return {
                'status': 404,
                'error': 'Tarea no encontrada',
                'message': f'No existe una tarea con ID {tarea_id}'
            }

    def crear_tarea(self, datos_tarea):
        """POST /tareas - Crea una nueva tarea"""
        # Validar campos requeridos
        campos_requeridos = ['titulo', 'descripcion', 'prioridad', 'asignado_a']
        for campo in campos_requeridos:
            if campo not in datos_tarea:
                return {
                    'status': 400,
                    'error': f'Campo requerido faltante: {campo}',
                    'message': 'No se pudo crear la tarea'
                }

        # Crear nueva tarea
        nueva_tarea = {
            'id': self.siguiente_id,
            'titulo': datos_tarea['titulo'],
            'descripcion': datos_tarea['descripcion'],
            'prioridad': datos_tarea.get('prioridad', 'media'),
            'estado': datos_tarea.get('estado', 'pendiente'),
            'fecha_creacion': datetime.now().strftime('%Y-%m-%d'),
            'fecha_vencimiento': datos_tarea.get('fecha_vencimiento', 
                (datetime.now() + timedelta(days=7)).strftime('%Y-%m-%d')),
            'asignado_a': datos_tarea['asignado_a'],
            'etiquetas': datos_tarea.get('etiquetas', [])
        }

        self.tareas.append(nueva_tarea)
        self.siguiente_id += 1

        return {
            'status': 201,
            'data': nueva_tarea,
            'message': 'Tarea creada exitosamente'
        }

    def actualizar_tarea(self, tarea_id, datos_actualizacion):
        """PUT /tareas/{id} - Actualiza una tarea"""
        for i, tarea in enumerate(self.tareas):
            if tarea['id'] == tarea_id:
                # Actualizar solo los campos proporcionados
                tarea_actualizada = {**tarea, **datos_actualizacion}
                tarea_actualizada['fecha_modificacion'] = datetime.now().strftime('%Y-%m-%d %H:%M:%S')
                self.tareas[i] = tarea_actualizada

                return {
                    'status': 200,
                    'data': tarea_actualizada,
                    'message': 'Tarea actualizada exitosamente'
                }

        return {
            'status': 404,
            'error': 'Tarea no encontrada',
            'message': f'No existe una tarea con ID {tarea_id}'
        }

    def eliminar_tarea(self, tarea_id):
        """DELETE /tareas/{id} - Elimina una tarea"""
        for i, tarea in enumerate(self.tareas):
            if tarea['id'] == tarea_id:
                tarea_eliminada = self.tareas.pop(i)
                return {
                    'status': 200,
                    'data': tarea_eliminada,
                    'message': f'Tarea "{tarea_eliminada["titulo"]}" eliminada exitosamente'
                }

        return {
            'status': 404,
            'error': 'Tarea no encontrada',
            'message': f'No existe una tarea con ID {tarea_id}'
        }

    def filtrar_tareas(self, **filtros):
        """Filtra tareas por diferentes criterios"""
        tareas_filtradas = self.tareas.copy()

        if 'estado' in filtros:
            tareas_filtradas = [t for t in tareas_filtradas if t['estado'] == filtros['estado']]

        if 'prioridad' in filtros:
            tareas_filtradas = [t for t in tareas_filtradas if t['prioridad'] == filtros['prioridad']]

        if 'asignado_a' in filtros:
            tareas_filtradas = [t for t in tareas_filtradas if t['asignado_a'] == filtros['asignado_a']]

        if 'etiqueta' in filtros:
            tareas_filtradas = [t for t in tareas_filtradas if filtros['etiqueta'] in t['etiquetas']]

        if 'vencidas' in filtros and filtros['vencidas']:
            hoy = datetime.now()
            tareas_filtradas = [
                t for t in tareas_filtradas 
                if datetime.strptime(t['fecha_vencimiento'], '%Y-%m-%d') < hoy
            ]

        return {
            'status': 200,
            'data': tareas_filtradas,
            'total': len(tareas_filtradas),
            'filtros_aplicados': filtros,
            'message': f'Se encontraron {len(tareas_filtradas)} tareas que coinciden con los filtros'
        }

    def obtener_estadisticas(self):
        """Obtiene estad√≠sticas del sistema de tareas"""
        total_tareas = len(self.tareas)

        # Contar por estado
        estados = {}
        for tarea in self.tareas:
            estado = tarea['estado']
            estados[estado] = estados.get(estado, 0) + 1

        # Contar por prioridad
        prioridades = {}
        for tarea in self.tareas:
            prioridad = tarea['prioridad']
            prioridades[prioridad] = prioridades.get(prioridad, 0) + 1

        # Tareas vencidas
        hoy = datetime.now()
        tareas_vencidas = [
            t for t in self.tareas 
            if datetime.strptime(t['fecha_vencimiento'], '%Y-%m-%d') < hoy and t['estado'] != 'completada'
        ]

        # Productividad por persona
        productividad = {}
        for tarea in self.tareas:
            persona = tarea['asignado_a']
            if persona not in productividad:
                productividad[persona] = {'total': 0, 'completadas': 0}
            productividad[persona]['total'] += 1
            if tarea['estado'] == 'completada':
                productividad[persona]['completadas'] += 1

        # Calcular porcentajes
        for persona, stats in productividad.items():
            stats['porcentaje_completado'] = (stats['completadas'] / stats['total'] * 100) if stats['total'] > 0 else 0

        return {
            'status': 200,
            'data': {
                'total_tareas': total_tareas,
                'distribucion_estados': estados,
                'distribucion_prioridades': prioridades,
                'tareas_vencidas': len(tareas_vencidas),
                'productividad_por_persona': productividad
            },
            'message': 'Estad√≠sticas generadas exitosamente'
        }

# Funci√≥n para mostrar respuesta de API de forma legible
def mostrar_respuesta(response, titulo):
    print(f'\n{titulo}')
    print('=' * len(titulo))

    if response['status'] >= 200 and response['status'] < 300:
        print(f'‚úÖ Status: {response["status"]} - {response["message"]}')
        if 'data' in response:
            if isinstance(response['data'], list):
                print(f'üìä Total de elementos: {len(response["data"])}')
                for item in response['data']:
                    if isinstance(item, dict) and 'titulo' in item:
                        estado_emoji = {'pendiente': '‚è≥', 'en_progreso': 'üîÑ', 'completada': '‚úÖ'}
                        prioridad_emoji = {'alta': 'üî¥', 'media': 'üü°', 'baja': 'üü¢'}
                        print(f'   {estado_emoji.get(item["estado"], "üìù")} {item["titulo"]} - {prioridad_emoji.get(item["prioridad"], "")} {item["asignado_a"]}')
            else:
                if isinstance(response['data'], dict) and 'titulo' in response['data']:
                    tarea = response['data']
                    print(f'üìù Tarea: {tarea["titulo"]}')
                    print(f'   Estado: {tarea["estado"]}')
                    print(f'   Asignado a: {tarea["asignado_a"]}')
                    if 'dias_restantes' in tarea:
                        if tarea['dias_restantes'] < 0:
                            print(f'   ‚ö†Ô∏è Vencida hace {abs(tarea["dias_restantes"])} d√≠as')
                        else:
                            print(f'   ‚è∞ Vence en {tarea["dias_restantes"]} d√≠as')
    else:
        print(f'‚ùå Status: {response["status"]} - {response["error"]}')
        print(f'   Mensaje: {response["message"]}')

# Demostraci√≥n completa del sistema
print('üéØ SISTEMA DE GESTI√ìN DE TAREAS CON API')
print('=' * 50)

gestor = GestorTareas()

# 1. Obtener todas las tareas
response = gestor.obtener_todas_tareas()
mostrar_respuesta(response, '1. OBTENER TODAS LAS TAREAS')

# 2. Obtener tarea espec√≠fica
response = gestor.obtener_tarea(1)
mostrar_respuesta(response, '2. OBTENER TAREA ESPEC√çFICA (ID: 1)')

# 3. Crear nueva tarea
nueva_tarea = {
    'titulo': 'Implementar autenticaci√≥n',
    'descripcion': 'Agregar sistema de login y registro',
    'prioridad': 'alta',
    'asignado_a': 'Mar√≠a Rodr√≠guez',
    'fecha_vencimiento': '2024-08-20',
    'etiquetas': ['desarrollo', 'seguridad', 'backend']
}
response = gestor.crear_tarea(nueva_tarea)
mostrar_respuesta(response, '3. CREAR NUEVA TAREA')

# 4. Actualizar tarea
actualizacion = {
    'estado': 'completada',
    'descripcion': 'Curso de Python b√°sico completado exitosamente'
}
response = gestor.actualizar_tarea(1, actualizacion)
mostrar_respuesta(response, '4. ACTUALIZAR TAREA (ID: 1)')

# 5. Filtrar tareas
print('\n5. FILTRAR TAREAS')
print('=' * 20)

# Tareas pendientes
response = gestor.filtrar_tareas(estado='pendiente')
print(f'\nüìã Tareas pendientes: {response["total"]}')
for tarea in response['data']:
    print(f'   ‚Ä¢ {tarea["titulo"]} - {tarea["asignado_a"]}')

# Tareas de alta prioridad
response = gestor.filtrar_tareas(prioridad='alta')
print(f'\nüî¥ Tareas de alta prioridad: {response["total"]}')
for tarea in response['data']:
    print(f'   ‚Ä¢ {tarea["titulo"]} - {tarea["estado"]}')

# Tareas asignadas a una persona
response = gestor.filtrar_tareas(asignado_a='Mar√≠a Rodr√≠guez')
print(f'\nüë§ Tareas de Mar√≠a Rodr√≠guez: {response["total"]}')
for tarea in response['data']:
    print(f'   ‚Ä¢ {tarea["titulo"]} - {tarea["estado"]}')

# 6. Estad√≠sticas
print('\n6. ESTAD√çSTICAS DEL SISTEMA')
print('=' * 30)
response = gestor.obtener_estadisticas()
stats = response['data']

print(f'üìä Total de tareas: {stats["total_tareas"]}')
print(f'‚ö†Ô∏è Tareas vencidas: {stats["tareas_vencidas"]}')

print('\nüìà Distribuci√≥n por estado:')
for estado, cantidad in stats['distribucion_estados'].items():
    print(f'   ‚Ä¢ {estado.replace("_", " ").title()}: {cantidad}')

print('\nüéØ Distribuci√≥n por prioridad:')
for prioridad, cantidad in stats['distribucion_prioridades'].items():
    print(f'   ‚Ä¢ {prioridad.title()}: {cantidad}')

print('\nüë• Productividad por persona:')
for persona, datos in stats['productividad_por_persona'].items():
    print(f'   ‚Ä¢ {persona}: {datos["completadas"]}/{datos["total"]} ({datos["porcentaje_completado"]:.1f}%)')

# 7. Eliminar tarea
response = gestor.eliminar_tarea(2)
mostrar_respuesta(response, '7. ELIMINAR TAREA (ID: 2)')

# 8. Estado final
response = gestor.obtener_todas_tareas()
mostrar_respuesta(response, '8. ESTADO FINAL DE TAREAS')
