# Prueba de validaci√≥n de emails
print('\n1. VALIDACI√ìN DE EMAILS:')
emails_prueba = ['test@example.com', 'invalid-email', 'user@domain.es', 'nombre.apellido@empresa.com.es']
for email in emails_prueba:
    valido = validar_email(email)
    print(f'  {email:<30} -> {"‚úì V√°lido" if valido else "‚úó Inv√°lido"}')

# Prueba de conversi√≥n de temperatura
print('\n2. CONVERSI√ìN DE TEMPERATURA:')
temperaturas = [(0, 'celsius', 'fahrenheit'), (100, 'celsius', 'fahrenheit'), (32, 'fahrenheit', 'celsius')]
for temp, desde, hacia in temperaturas:
    resultado = convertir_temperatura(temp, desde, hacia)
    print(f'  {temp}¬∞ {desde.title()} = {resultado:.1f}¬∞ {hacia.title()}')

# Prueba de generaci√≥n de contrase√±as
print('\n3. GENERACI√ìN DE CONTRASE√ëAS:')
for longitud in [8, 12, 16]:
    password = generar_password(longitud, True)
    print(f'  Contrase√±a de {longitud} caracteres: {password}')

# Prueba de c√°lculo de IMC
print('\n4. C√ÅLCULO DE IMC:')
personas = [(70, 1.75), (80, 1.80), (60, 1.65)]
for peso, altura in personas:
    resultado = calcular_imc(peso, altura)
    print(f'  Peso: {peso}kg, Altura: {altura}m -> IMC: {resultado["imc"]} ({resultado["categoria"]})')

# Prueba de validaciones
print('\n5. VALIDACIONES:')
validador = ValidadorDatos()

# Tel√©fonos
telefonos = ['+34666123456', '666123456', '912345678', '123456']
print('  Tel√©fonos:')
for tel in telefonos:
    valido = validador.es_numero_telefono(tel)
    print(f'    {tel:<15} -> {"‚úì V√°lido" if valido else "‚úó Inv√°lido"}')

# C√≥digos postales
codigos = ['28001', '08001', '41001', '1234', '123456']
print('  C√≥digos postales:')
for codigo in codigos:
    valido = validador.es_codigo_postal(codigo)
    print(f'    {codigo:<10} -> {"‚úì V√°lido" if valido else "‚úó Inv√°lido"}')

# DNIs
dnis = ['12345678Z', '12345678A', '87654321X']
print('  DNIs:')
for dni in dnis:
    valido = validador.es_dni_valido(dni)
    print(f'    {dni:<12} -> {"‚úì V√°lido" if valido else "‚úó Inv√°lido"}')

print('\n6. CONVERSIONES DE UNIDADES:')
conversor = ConversorUnidades()
